import React, { useState } from "react";

function CreateArea() {
  const [input, setInput] = useState("")
  function captureInput(event) {
    const {value, name} = event.target
    setInput(value)
  }

  const isDone = false

  function addNote() {
    setInput(value)
  }

  return (
    <div>
      <form>
        <input value={input} onChange={captureInput} name="title" placeholder="Title" />
        <textarea value={input} onChange={captureInput} name="content" placeholder="Take a note..." rows="3" />
        <button onClick={addNote}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
